let x=0,y=0,fx=0,fy=0,score=0;const box=document.getElementById("box"),frog=document.getElementById("frog");function frogjump(){fx=Math.floor(1366*Math.random()),fx=y=Math.floor(768*Math.random()),frog.style.top=fy+"px",frog.style.top=fx+"px"}function moveBox(direction){switch(direction){case"up":y-=10;break;case"down":y+=10;break;case"right":x+=10;break;case"left":x-=10}box.style.top=y+"px",box.style.left=x+"px"}document.addEventListener("keydown",(function(event){switch(event.key){case"ArrowUP":case"w":moveBox("up");break;case"ArrowDown":case"s":moveBox("down");break;case"ArrowRight":case"d":moveBox("right");break;case"ArrowLeft":case"a":moveBox("left");break;case"j":fy+=10,fx-=10,frogjump("jump")}}));